select * from ceo_store where
sname like CONCAT('%',#{keyword},'%') or Ftype like CONCAT('%',#{keyword},'%') or location like CONCAT('%',#{keyword},'%')
(sname like CONCAT('%',#{keyword},'%') or Ftype like CONCAT('%',#{keyword},'%') or location like CONCAT('%',#{keyword},'%')) and weather=#{tmp}
(sname like CONCAT('%',#{keyword},'%') or Ftype like CONCAT('%',#{keyword},'%') or location like CONCAT('%',#{keyword},'%')) and yearS=#{tmp}
(sname like CONCAT('%',#{keyword},'%') or Ftype like CONCAT('%',#{keyword},'%') or location like CONCAT('%',#{keyword},'%')) and area = #{tmp}
(sname like CONCAT('%',#{keyword},'%') or Ftype like CONCAT('%',#{keyword},'%') or location like CONCAT('%',#{keyword},'%')) and priceS = #{tmp}
(sname like CONCAT('%',#{keyword},'%') or Ftype like CONCAT('%',#{keyword},'%') or location like CONCAT('%',#{keyword},'%')) and lvs = #{tmp}
order by sid DESC limit #{pageStart}, #{perPageNum}

